import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import Home from './components/Home';
import Register from './components/auth/Register';
import Login from './components/auth/Login';
import CourseList from './components/courses/CourseList';
import CreateCourse from './components/courses/CreateCourse';
import PostDetail from './components/posts/PostDetail';
import CourseDetail from './components/courses/CourseDetail';
import Profile from './components/profile/Profile';
import Navbar from './components/Navbar';
import ItemList from './components/marketplace/ItemList';
import CreateItem from './components/marketplace/CreateItem';
import ItemDetail from './components/marketplace/ItemDetail';
import Cart from './components/cart/Cart';
import { CartProvider } from './context/CartContext';

const PrivateRoute = ({ children }) => {
  const token = localStorage.getItem('token');
  return token ? children : <Navigate to="/login" />;
};

function App() {
  return (
    <CartProvider>
      <Router>
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />
          <Route path="/courses" element={<CourseList />} />
          <Route
            path="/courses/create"
            element={
              <PrivateRoute>
                <CreateCourse />
              </PrivateRoute>
            }
          />
          <Route path="/posts/:id" element={<PostDetail />} />
          <Route path="/courses/:id" element={<CourseDetail />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/marketplace" element={<ItemList />} />
          <Route path="/marketplace/create" element={<CreateItem />} />
          <Route path="/marketplace/:id" element={<ItemDetail />} />
          <Route path="/cart" element={<Cart />} />
        </Routes>
      </Router>
    </CartProvider>
  );
}

export default App;
